import React, { useMemo } from 'react';
import { TRANSACTION_TYPES } from '../db';

function Dashboard({ transactions }) {
  const stats = useMemo(() => {
    const result = {
      totalIncome: 0,
      totalExpense: 0,
      balance: 0,
      recentTransactions: [],
      categorySummary: {
        income: {},
        expense: {}
      }
    };

    if (transactions && transactions.length > 0) {
      transactions.forEach(transaction => {
        if (transaction.type === TRANSACTION_TYPES.INCOME) {
          result.totalIncome += Number(transaction.amount);
          // 统计收入分类
          if (!result.categorySummary.income[transaction.category]) {
            result.categorySummary.income[transaction.category] = 0;
          }
          result.categorySummary.income[transaction.category] += Number(transaction.amount);
        } else {
          result.totalExpense += Number(transaction.amount);
          // 统计支出分类
          if (!result.categorySummary.expense[transaction.category]) {
            result.categorySummary.expense[transaction.category] = 0;
          }
          result.categorySummary.expense[transaction.category] += Number(transaction.amount);
        }
      });

      // 计算余额
      result.balance = result.totalIncome - result.totalExpense;

      // 获取最近10条交易
      result.recentTransactions = [...transactions]
        .sort((a, b) => new Date(b.date) - new Date(a.date))
        .slice(0, 5);
    }
    
    return result;
  }, [transactions]);

  // 格式化金额为人民币格式
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('zh-CN', {
      style: 'currency',
      currency: 'CNY'
    }).format(amount);
  };

  // 格式化日期
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('zh-CN');
  };

  return (
    <div>
      <div className="card">
        <h2>账户概览</h2>
        <div className="flex justify-between" style={{ marginTop: '20px' }}>
          <div>
            <h3>总收入</h3>
            <p className="income" style={{ fontSize: '24px', fontWeight: 'bold' }}>
              {formatCurrency(stats.totalIncome)}
            </p>
          </div>
          <div>
            <h3>总支出</h3>
            <p className="expense" style={{ fontSize: '24px', fontWeight: 'bold' }}>
              {formatCurrency(stats.totalExpense)}
            </p>
          </div>
          <div>
            <h3>结余</h3>
            <p 
              style={{ 
                fontSize: '24px', 
                fontWeight: 'bold',
                color: stats.balance >= 0 ? '#4caf50' : '#f44336'
              }}
            >
              {formatCurrency(stats.balance)}
            </p>
          </div>
        </div>
      </div>

      <div className="card">
        <h2>最近记录</h2>
        {stats.recentTransactions.length > 0 ? (
          <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '15px' }}>
            <thead>
              <tr style={{ borderBottom: '1px solid #ddd' }}>
                <th style={{ textAlign: 'left', padding: '8px' }}>日期</th>
                <th style={{ textAlign: 'left', padding: '8px' }}>类型</th>
                <th style={{ textAlign: 'left', padding: '8px' }}>类别</th>
                <th style={{ textAlign: 'left', padding: '8px' }}>描述</th>
                <th style={{ textAlign: 'right', padding: '8px' }}>金额</th>
              </tr>
            </thead>
            <tbody>
              {stats.recentTransactions.map(transaction => (
                <tr 
                  key={transaction.id}
                  style={{ borderBottom: '1px solid #eee' }}
                >
                  <td style={{ padding: '8px' }}>{formatDate(transaction.date)}</td>
                  <td style={{ padding: '8px' }}>{transaction.type}</td>
                  <td style={{ padding: '8px' }}>{transaction.category}</td>
                  <td style={{ padding: '8px' }}>{transaction.description}</td>
                  <td 
                    style={{ 
                      padding: '8px', 
                      textAlign: 'right',
                      color: transaction.type === TRANSACTION_TYPES.INCOME ? '#4caf50' : '#f44336'
                    }}
                  >
                    {formatCurrency(transaction.amount)}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p style={{ textAlign: 'center', margin: '20px 0' }}>暂无交易记录</p>
        )}
      </div>

      <div className="flex justify-between" style={{ gap: '20px' }}>
        <div className="card" style={{ flex: 1 }}>
          <h2>收入分类统计</h2>
          {Object.keys(stats.categorySummary.income).length > 0 ? (
            <ul style={{ listStyle: 'none', marginTop: '15px' }}>
              {Object.entries(stats.categorySummary.income)
                .sort((a, b) => b[1] - a[1])
                .map(([category, amount]) => (
                  <li 
                    key={category}
                    style={{ 
                      display: 'flex', 
                      justifyContent: 'space-between',
                      padding: '8px 0',
                      borderBottom: '1px solid #eee'
                    }}
                  >
                    <span>{category}</span>
                    <span className="income">{formatCurrency(amount)}</span>
                  </li>
                ))}
            </ul>
          ) : (
            <p style={{ textAlign: 'center', margin: '20px 0' }}>暂无收入记录</p>
          )}
        </div>

        <div className="card" style={{ flex: 1 }}>
          <h2>支出分类统计</h2>
          {Object.keys(stats.categorySummary.expense).length > 0 ? (
            <ul style={{ listStyle: 'none', marginTop: '15px' }}>
              {Object.entries(stats.categorySummary.expense)
                .sort((a, b) => b[1] - a[1])
                .map(([category, amount]) => (
                  <li 
                    key={category}
                    style={{ 
                      display: 'flex', 
                      justifyContent: 'space-between',
                      padding: '8px 0',
                      borderBottom: '1px solid #eee'
                    }}
                  >
                    <span>{category}</span>
                    <span className="expense">{formatCurrency(amount)}</span>
                  </li>
                ))}
            </ul>
          ) : (
            <p style={{ textAlign: 'center', margin: '20px 0' }}>暂无支出记录</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
