import React, { useState, useMemo } from 'react';
import { deleteTransaction, TRANSACTION_TYPES } from '../db';

function TransactionList({ transactions, onEdit, onDelete }) {
  const [filter, setFilter] = useState({
    type: 'all', // 'all', 'income', 'expense'
    search: '',
    sortBy: 'date', // 'date', 'amount', 'category'
    sortDirection: 'desc' // 'asc', 'desc'
  });

  // 筛选和排序交易列表
  const filteredTransactions = useMemo(() => {
    if (!transactions || transactions.length === 0) return [];
    
    let result = [...transactions];
    
    // 根据类型筛选
    if (filter.type !== 'all') {
      const typeFilter = filter.type === 'income' ? TRANSACTION_TYPES.INCOME : TRANSACTION_TYPES.EXPENSE;
      result = result.filter(t => t.type === typeFilter);
    }
    
    // 根据搜索词筛选
    if (filter.search.trim() !== '') {
      const searchLower = filter.search.toLowerCase().trim();
      result = result.filter(t => 
        t.description?.toLowerCase().includes(searchLower) ||
        t.category?.toLowerCase().includes(searchLower)
      );
    }
    
    // 排序
    result.sort((a, b) => {
      let comparison = 0;
      
      switch (filter.sortBy) {
        case 'date':
          comparison = new Date(a.date) - new Date(b.date);
          break;
        case 'amount':
          comparison = Number(a.amount) - Number(b.amount);
          break;
        case 'category':
          comparison = a.category.localeCompare(b.category);
          break;
        default:
          comparison = new Date(a.date) - new Date(b.date);
      }
      
      return filter.sortDirection === 'asc' ? comparison : -comparison;
    });
    
    return result;
  }, [transactions, filter]);

  // 处理删除交易
  const handleDelete = async (id) => {
    if (window.confirm('确定要删除这条记录吗？')) {
      try {
        await deleteTransaction(id);
        onDelete(); // 刷新列表
        alert('记录已成功删除');
      } catch (error) {
        console.error('删除记录失败:', error);
        alert('删除记录失败，请重试');
      }
    }
  };

  // 处理排序变化
  const handleSortChange = (sortBy) => {
    setFilter(prev => ({
      ...prev,
      sortBy,
      sortDirection: prev.sortBy === sortBy && prev.sortDirection === 'desc' ? 'asc' : 'desc'
    }));
  };

  // 格式化金额为人民币格式
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('zh-CN', {
      style: 'currency',
      currency: 'CNY'
    }).format(amount);
  };

  // 格式化日期
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('zh-CN');
  };

  // 渲染排序箭头
  const renderSortArrow = (column) => {
    if (filter.sortBy !== column) return null;
    return filter.sortDirection === 'asc' ? ' ↑' : ' ↓';
  };

  return (
    <div className="card">
      <h2>交易记录</h2>
      
      <div style={{ marginBottom: '20px' }}>
        <div className="flex justify-between" style={{ marginBottom: '10px' }}>
          <div>
            <label style={{ marginRight: '10px' }}>类型过滤:</label>
            <select 
              value={filter.type}
              onChange={(e) => setFilter(prev => ({ ...prev, type: e.target.value }))}
              className="form-control"
              style={{ display: 'inline-block', width: 'auto', marginRight: '15px' }}
            >
              <option value="all">全部</option>
              <option value="income">收入</option>
              <option value="expense">支出</option>
            </select>
          </div>
          
          <div>
            <input 
              type="text" 
              placeholder="搜索描述或类别..." 
              value={filter.search}
              onChange={(e) => setFilter(prev => ({ ...prev, search: e.target.value }))}
              className="form-control"
              style={{ display: 'inline-block', width: '200px' }}
            />
          </div>
        </div>
      </div>
      
      {filteredTransactions.length > 0 ? (
        <div style={{ overflowX: 'auto' }}>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ borderBottom: '2px solid #ddd' }}>
                <th 
                  style={{ padding: '10px', textAlign: 'left', cursor: 'pointer' }}
                  onClick={() => handleSortChange('date')}
                >
                  日期{renderSortArrow('date')}
                </th>
                <th style={{ padding: '10px', textAlign: 'left' }}>类型</th>
                <th 
                  style={{ padding: '10px', textAlign: 'left', cursor: 'pointer' }}
                  onClick={() => handleSortChange('category')}
                >
                  类别{renderSortArrow('category')}
                </th>
                <th style={{ padding: '10px', textAlign: 'left' }}>描述</th>
                <th 
                  style={{ padding: '10px', textAlign: 'right', cursor: 'pointer' }}
                  onClick={() => handleSortChange('amount')}
                >
                  金额{renderSortArrow('amount')}
                </th>
                <th style={{ padding: '10px', textAlign: 'center' }}>操作</th>
              </tr>
            </thead>
            <tbody>
              {filteredTransactions.map(transaction => (
                <tr 
                  key={transaction.id}
                  style={{ borderBottom: '1px solid #eee' }}
                >
                  <td style={{ padding: '10px' }}>{formatDate(transaction.date)}</td>
                  <td style={{ padding: '10px' }}>{transaction.type}</td>
                  <td style={{ padding: '10px' }}>{transaction.category}</td>
                  <td style={{ padding: '10px' }}>{transaction.description}</td>
                  <td 
                    style={{ 
                      padding: '10px', 
                      textAlign: 'right',
                      color: transaction.type === TRANSACTION_TYPES.INCOME ? '#4caf50' : '#f44336'
                    }}
                  >
                    {formatCurrency(transaction.amount)}
                  </td>
                  <td style={{ padding: '10px', textAlign: 'center' }}>
                    <div style={{ display: 'flex', justifyContent: 'center', gap: '10px' }}>
                      <button 
                        className="btn btn-secondary" 
                        style={{ padding: '5px 10px' }}
                        onClick={() => onEdit(transaction)}
                      >
                        编辑
                      </button>
                      <button 
                        className="btn btn-danger" 
                        style={{ padding: '5px 10px' }}
                        onClick={() => handleDelete(transaction.id)}
                      >
                        删除
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p style={{ textAlign: 'center', margin: '30px 0' }}>
          {filter.search || filter.type !== 'all' 
            ? '没有找到匹配的记录' 
            : '暂无交易记录'}
        </p>
      )}

      <div style={{ marginTop: '20px', textAlign: 'right' }}>
        <p>共 {filteredTransactions.length} 条记录</p>
      </div>
    </div>
  );
}

export default TransactionList;
